import requests, bs4

for i in range(1,31):
    resp = requests.get('http://localhost:8000/golfer/'+str(i))
    golfer = bs4.BeautifulSoup(resp.text,"html.parser")
    golfer_name = golfer.find('h2').text
    golfer_td_list = golfer.select('td')
    print("Golfer: " + golfer_name + "\n")

    for index in range(0,len(golfer_td_list), 2):
        tourn_name = golfer_td_list[index].getText()
        golfer_tourn_score = golfer_td_list[index+1].getText().strip()
        row = tourn_name.ljust(21) + " " + golfer_tourn_score
        print(row)
        if index == 0:
            print("----------            -----------")

    print("\n---------------------------------\n")
